//go:build ignore

package main

import (
	"fmt"
	"math/big"
	"os"

	"github.com/bnb-chain/tss-lib/v2/crypto/paillier"
	"github.com/bnb-chain/tss-lib/v2/ecdsa/keygen"
)

func main() {
	paillierN, _ := new(big.Int).SetString("", 10)
	paillierLambda, _ := new(big.Int).SetString("", 10)
	paillierPhi, _ := new(big.Int).SetString("", 10)
	paillierP, _ := new(big.Int).SetString("", 10)
	paillierQ, _ := new(big.Int).SetString("", 10)

	nTilde, _ := new(big.Int).SetString("", 10)
	h1, _ := new(big.Int).SetString("", 10)
	h2, _ := new(big.Int).SetString("", 10)

	preParams := &keygen.LocalPreParams{
		PaillierSK: &paillier.PrivateKey{
			PublicKey: paillier.PublicKey{
				N: paillierN,
			},
			LambdaN: paillierLambda,
			PhiN:    paillierPhi,
			P:       paillierP,
			Q:       paillierQ,
		},
		NTildei: nTilde,
		H1i:     h1,
		H2i:     h2,
	}

	fmt.Println("Testing pre-params validation...")
	if err := preParams.Validate(); err != nil {
		fmt.Printf("Validation FAILED: %v\n", err)
		os.Exit(1)
	}
	fmt.Println("Validation PASSED!")
}
